import React, { useState, useEffect } from 'react';
import { HashLink as Link } from 'react-router-hash-link';

export default function Header() {
  const [mobileToggle, setMobileToggle] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className={`header-top-fixed one-page-nav ${mobileToggle ? 'menu-open' : ''} ${scrolled ? 'fixed-header' : ''}`}>
      <div className="container">
        <div className="logo">
          <Link to="#home" smooth>
            <img src="/images/portfolioNameLogo.png" alt="Logo" />
          </Link>
        </div>
        <ul className="main-menu">
          <li><Link to="#home" smooth onClick={() => setMobileToggle(false)}>Home</Link></li>
          <li><Link to="#about" smooth onClick={() => setMobileToggle(false)}>About Me</Link></li>
          <li><Link to="#education" smooth onClick={() => setMobileToggle(false)}>Education</Link></li>
          <li><Link to="#skills" smooth onClick={() => setMobileToggle(false)}>Skills</Link></li>
          <li><Link to="#projects" smooth onClick={() => setMobileToggle(false)}>Projects</Link></li>
        </ul>
        <div className="d-flex">
          <Link to="#contactus" smooth className="px-btn d-none d-lg-inline-flex" onClick={() => setMobileToggle(false)}>Connect with me</Link>
          <button className="toggler-menu d-lg-none" onClick={() => setMobileToggle(!mobileToggle)}>
            <span />
          </button>
        </div>
      </div>
    </div>
  );
}
